import { type User, type InsertUser, type EmotionDetection, type InsertEmotionDetection, type Feedback, type InsertFeedback } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  addEmotionDetection(detection: InsertEmotionDetection): Promise<EmotionDetection>;
  getRecentEmotions(limit: number): Promise<EmotionDetection[]>;
  getEmotionStats(): Promise<{ emotion: string; count: number; percentage: number }[]>;
  getTrendData(): Promise<Array<{ time: string; happiness: number; positive: number; negative: number }>>;

  addFeedback(feedback: InsertFeedback): Promise<Feedback>;
  getRecentFeedback(limit: number): Promise<Feedback[]>;
  updateFeedbackSentiment(id: string, sentiment: string): Promise<void>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private emotions: EmotionDetection[];
  private feedbacks: Feedback[];

  constructor() {
    this.users = new Map();
    this.emotions = [];
    this.feedbacks = [];
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async addEmotionDetection(detection: InsertEmotionDetection): Promise<EmotionDetection> {
    const emotion: EmotionDetection = {
      id: randomUUID(),
      emotion: detection.emotion,
      confidence: detection.confidence,
      timestamp: new Date(),
    };
    this.emotions.push(emotion);

    // Keep only last 1000 emotions
    if (this.emotions.length > 1000) {
      this.emotions = this.emotions.slice(-1000);
    }

    return emotion;
  }

  async getRecentEmotions(limit: number): Promise<EmotionDetection[]> {
    return this.emotions.slice(-limit).reverse();
  }

  async getEmotionStats(): Promise<{ emotion: string; count: number; percentage: number }[]> {
    const emotionCounts = new Map<string, number>();
    const total = this.emotions.length;

    this.emotions.forEach(e => {
      emotionCounts.set(e.emotion, (emotionCounts.get(e.emotion) || 0) + 1);
    });

    return Array.from(emotionCounts.entries())
      .map(([emotion, count]) => ({
        emotion,
        count,
        percentage: total > 0 ? Math.round((count / total) * 100) : 0,
      }))
      .sort((a, b) => b.count - a.count);
  }

  async addFeedback(feedback: InsertFeedback): Promise<Feedback> {
    const newFeedback: Feedback = {
      id: randomUUID(),
      ...feedback,
      sentiment: null,
      timestamp: new Date(),
    };
    this.feedbacks.push(newFeedback);

    // Keep only last 100 feedbacks
    if (this.feedbacks.length > 100) {
      this.feedbacks = this.feedbacks.slice(-100);
    }

    return newFeedback;
  }

  async getRecentFeedback(limit: number): Promise<Feedback[]> {
    return this.feedbacks.slice(-limit).reverse();
  }

  async updateFeedbackSentiment(id: string, sentiment: string): Promise<void> {
    const feedback = this.feedbacks.find(f => f.id === id);
    if (feedback) {
      feedback.sentiment = sentiment;
    }
  }

  async getTrendData(): Promise<Array<{ time: string; happiness: number; positive: number; negative: number }>> {
    if (this.emotions.length === 0) {
      // Return empty trend if no data
      return [];
    }

    // Emotion weights for happiness calculation
    const weights: Record<string, number> = {
      Delighted: 100,
      Happy: 85,
      Neutral: 60,
      Frustrated: 35,
      Angry: 10,
    };

    // Group emotions into time buckets (last 24 data points or hours)
    const bucketCount = Math.min(24, this.emotions.length);
    const emotionsPerBucket = Math.max(1, Math.floor(this.emotions.length / bucketCount));

    const trendData = [];

    for (let i = 0; i < bucketCount; i++) {
      const startIdx = i * emotionsPerBucket;
      const endIdx = Math.min((i + 1) * emotionsPerBucket, this.emotions.length);
      const bucketEmotions = this.emotions.slice(startIdx, endIdx);

      if (bucketEmotions.length === 0) continue;

      // Calculate happiness score for this bucket
      let totalWeight = 0;
      let positiveCount = 0;
      let negativeCount = 0;

      bucketEmotions.forEach(e => {
        const weight = weights[e.emotion] || 60;
        totalWeight += weight;

        if (e.emotion === 'Happy' || e.emotion === 'Delighted') {
          positiveCount++;
        } else if (e.emotion === 'Frustrated' || e.emotion === 'Angry') {
          negativeCount++;
        }
      });

      const happiness = Math.round(totalWeight / bucketEmotions.length);
      const positive = Math.round((positiveCount / bucketEmotions.length) * 100);
      const negative = Math.round((negativeCount / bucketEmotions.length) * 100);

      // Format time based on bucket
      const timestamp = bucketEmotions[bucketEmotions.length - 1].timestamp;
      const time = timestamp.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false });

      trendData.push({ time, happiness, positive, negative });
    }

    return trendData;
  }
}

export const storage = new MemStorage();

