import { Card } from "@/components/ui/card";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from "recharts";

interface TrendChartProps {
  title: string;
  data?: any[];
}

export default function TrendChart({ title, data }: TrendChartProps) {
  const chartData = data && data.length > 0 ? data : null;

  return (
    <Card className="p-6">
      <div className="text-xs uppercase font-medium text-muted-foreground tracking-wider mb-6">
        {title}
      </div>

      {chartData ? (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
            <XAxis
              dataKey="time"
              stroke="hsl(var(--muted-foreground))"
              fontSize={12}
              tickLine={false}
            />
            <YAxis
              stroke="hsl(var(--muted-foreground))"
              fontSize={12}
              tickLine={false}
            />
            <Tooltip
              contentStyle={{
                backgroundColor: "hsl(var(--popover))",
                border: "1px solid hsl(var(--border))",
                borderRadius: "0.5rem",
                fontSize: "12px",
              }}
            />
            <Legend
              wrapperStyle={{
                fontSize: "12px",
              }}
            />
            <Line
              type="monotone"
              dataKey="happiness"
              stroke="hsl(var(--primary))"
              strokeWidth={2}
              dot={false}
              name="Happiness Score"
            />
            <Line
              type="monotone"
              dataKey="positive"
              stroke="hsl(var(--chart-5))"
              strokeWidth={2}
              dot={false}
              name="Positive Emotions"
            />
            <Line
              type="monotone"
              dataKey="negative"
              stroke="hsl(var(--destructive))"
              strokeWidth={2}
              dot={false}
              name="Negative Emotions"
            />
          </LineChart>
        </ResponsiveContainer>
      ) : (
        <div className="flex items-center justify-center h-[300px] text-muted-foreground">
          <div className="text-center">
            <p className="text-sm">No trend data available yet</p>
            <p className="text-xs mt-1">Start the camera to begin collecting emotion data</p>
          </div>
        </div>
      )}
    </Card>
  );
}

