import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer } from "ws";
import { storage } from "./storage";
import { analyzeImageEmotion, analyzeFeedbackSentiment, generateInsights } from "./gemini";
import { insertEmotionDetectionSchema, insertFeedbackSchema } from "@shared/schema";

// HACKATHON DEMO MODE - simulates realistic emotions without API calls
// Set DEMO_MODE=false in environment variables to use real Gemini API
// Defaults to true for hackathon presentations (no API quota limits)
const DEMO_MODE = process.env.DEMO_MODE !== "false";

// Log demo mode status on startup
if (DEMO_MODE) {
  console.log("🎭 HACKATHON DEMO MODE ENABLED - Using simulated emotions and insights");
  console.log("   To use real Gemini API, set environment variable: DEMO_MODE=false");
} else {
  console.log("🚀 PRODUCTION MODE - Using real Gemini API");
}

const demoEmotions = [
  { emotion: "Happy", confidence: 85 },
  { emotion: "Delighted", confidence: 90 },
  { emotion: "Neutral", confidence: 75 },
  { emotion: "Happy", confidence: 82 },
  { emotion: "Frustrated", confidence: 70 },
  { emotion: "Neutral", confidence: 80 },
  { emotion: "Happy", confidence: 88 },
  { emotion: "Delighted", confidence: 92 },
  { emotion: "Frustrated", confidence: 68 },
  { emotion: "Angry", confidence: 75 },
  { emotion: "Neutral", confidence: 70 },
  { emotion: "Happy", confidence: 86 },
  { emotion: "Delighted", confidence: 88 },
  { emotion: "Frustrated", confidence: 72 },
  { emotion: "Happy", confidence: 90 },
];

let demoIndex = 0;

function getDemoEmotion() {
  const emotion = demoEmotions[demoIndex];
  demoIndex = (demoIndex + 1) % demoEmotions.length;
  return emotion;
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Emotion detection endpoint
  app.post("/api/emotions/analyze", async (req, res) => {
    try {
      const { image } = req.body;

      if (!image) {
        return res.status(400).json({ error: "Image data required" });
      }

      let emotionAnalysis;

      if (DEMO_MODE) {
        // Demo mode - return varied realistic emotions
        console.log("🎭 DEMO MODE: Simulating emotion detection");
        const demoData = getDemoEmotion();
        emotionAnalysis = { emotion: demoData.emotion, confidence: demoData.confidence };
        // Small delay to simulate processing
        await new Promise(resolve => setTimeout(resolve, 300));
      } else {
        // Real Gemini API mode
        const base64Image = image.replace(/^data:image\/\w+;base64,/, "");
        emotionAnalysis = await analyzeImageEmotion(base64Image);
      }

      // Store the detection
      const detection = await storage.addEmotionDetection({
        emotion: emotionAnalysis.emotion,
        confidence: emotionAnalysis.confidence,
      });

      // Broadcast to WebSocket clients
      broadcastEmotionUpdate(detection);

      res.json(detection);
    } catch (error: any) {
      console.error("Error analyzing emotion:", error);
      res.status(500).json({ error: error.message });
    }
  });

  // Get recent emotions
  app.get("/api/emotions/recent", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 10;
      const emotions = await storage.getRecentEmotions(limit);
      res.json(emotions);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

  // Get emotion statistics
  app.get("/api/emotions/stats", async (req, res) => {
    try {
      const stats = await storage.getEmotionStats();
      res.json(stats);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

  // Submit feedback
  app.post("/api/feedback", async (req, res) => {
    try {
      const validatedData = insertFeedbackSchema.parse(req.body);

      // Create feedback
      const feedback = await storage.addFeedback(validatedData);

      if (DEMO_MODE) {
        // Demo mode - simple sentiment analysis
        console.log("🎭 DEMO MODE: Simulating sentiment analysis");
        const text = validatedData.text.toLowerCase();
        let sentiment = "Neutral";

        if (text.includes("great") || text.includes("excellent") || text.includes("love") || text.includes("amazing") || text.includes("good")) {
          sentiment = "Positive";
        } else if (text.includes("bad") || text.includes("terrible") || text.includes("awful") || text.includes("poor") || text.includes("slow")) {
          sentiment = "Negative";
        } else if (text.includes("okay") || text.includes("fine") || text.includes("average")) {
          sentiment = "Mixed";
        }

        setTimeout(async () => {
          await storage.updateFeedbackSentiment(feedback.id, sentiment);
          broadcastFeedbackUpdate({ ...feedback, sentiment });
        }, 1000);
      } else {
        // Real Gemini API mode
        analyzeFeedbackSentiment(validatedData.text)
          .then(async (result) => {
            await storage.updateFeedbackSentiment(feedback.id, result.sentiment);
            broadcastFeedbackUpdate({ ...feedback, sentiment: result.sentiment });
          })
          .catch(console.error);
      }

      res.json(feedback);
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  });

  // Get recent feedback
  app.get("/api/feedback/recent", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 10;
      const feedback = await storage.getRecentFeedback(limit);
      res.json(feedback);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

  // Generate AI insights
  app.get("/api/insights", async (req, res) => {
    try {
      const emotionStats = await storage.getEmotionStats();
      const recentFeedback = await storage.getRecentFeedback(5);

      let insights;

      if (DEMO_MODE) {
        // Demo mode - return pre-crafted insights based on data
        console.log("🎭 DEMO MODE: Generating demo insights");
        insights = generateDemoInsights(emotionStats, recentFeedback);
      } else {
        // Real Gemini API mode
        insights = await generateInsights(emotionStats, recentFeedback);
      }

      res.json(insights);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

  // Calculate happiness score
  app.get("/api/happiness-score", async (req, res) => {
    try {
      const stats = await storage.getEmotionStats();

      // Calculate weighted score
      const weights: Record<string, number> = {
        Delighted: 100,
        Happy: 85,
        Neutral: 60,
        Frustrated: 35,
        Angry: 10,
      };

      let totalWeight = 0;
      let totalCount = 0;

      stats.forEach(stat => {
        const weight = weights[stat.emotion] || 60;
        totalWeight += weight * stat.count;
        totalCount += stat.count;
      });

      const score = totalCount > 0 ? Math.round(totalWeight / totalCount) : 75;

      res.json({ score, totalInteractions: totalCount });
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

  // Get trend data for chart
  app.get("/api/trends", async (req, res) => {
    try {
      const trendData = await storage.getTrendData();
      res.json(trendData);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

  const httpServer = createServer(app);

  // WebSocket setup
  const wss = new WebSocketServer({ server: httpServer, path: "/ws" });

  wss.on("connection", (ws) => {
    console.log("WebSocket client connected");

    ws.on("close", () => {
      console.log("WebSocket client disconnected");
    });
  });

  // Store WSS for broadcasting
  (global as any).wss = wss;

  return httpServer;
}

function generateDemoInsights(emotionStats: any[], recentFeedback: any[]) {
  const insights = [];

  // Calculate emotion percentages
  const totalCount = emotionStats.reduce((sum, stat) => sum + stat.count, 0);
  const happyCount = emotionStats.filter(s => s.emotion === "Happy" || s.emotion === "Delighted").reduce((sum, s) => sum + s.count, 0);
  const negativeCount = emotionStats.filter(s => s.emotion === "Frustrated" || s.emotion === "Angry").reduce((sum, s) => sum + s.count, 0);

  const happyPercent = totalCount > 0 ? Math.round((happyCount / totalCount) * 100) : 0;
  const negativePercent = totalCount > 0 ? Math.round((negativeCount / totalCount) * 100) : 0;

  // Insight 1: Overall sentiment finding
  if (happyPercent > 60) {
    insights.push({
      type: "finding",
      title: "Strong Positive Customer Sentiment",
      description: `${happyPercent}% of customers showing happy or delighted expressions, indicating excellent service quality and customer satisfaction.`,
      priority: "medium"
    });
  } else if (negativePercent > 30) {
    insights.push({
      type: "finding",
      title: "Elevated Customer Frustration Detected",
      description: `${negativePercent}% of interactions show frustration or dissatisfaction. Immediate attention needed to address service gaps.`,
      priority: "high"
    });
  } else {
    insights.push({
      type: "finding",
      title: "Balanced Customer Experience",
      description: `Mixed sentiment detected with ${happyPercent}% positive and ${negativePercent}% negative emotions, suggesting room for improvement.`,
      priority: "medium"
    });
  }

  // Insight 2: Actionable recommendation
  if (negativePercent > 20) {
    insights.push({
      type: "recommendation",
      title: "Deploy Additional Staff During Peak Hours",
      description: "Customer frustration correlates with wait times. Adding 2-3 service representatives during high-traffic periods could reduce negative sentiment by 40%.",
      priority: "high"
    });
  } else {
    insights.push({
      type: "recommendation",
      title: "Maintain Current Service Standards",
      description: "Current staffing levels and service protocols are working well. Continue monitoring for consistency across all locations.",
      priority: "low"
    });
  }

  // Insight 3: Feedback-based insight
  if (recentFeedback.length > 0) {
    const positiveFeedback = recentFeedback.filter(f => f.sentiment === "Positive").length;
    if (positiveFeedback > recentFeedback.length / 2) {
      insights.push({
        type: "finding",
        title: "Customer Feedback Highly Positive",
        description: `${positiveFeedback} out of ${recentFeedback.length} recent feedback submissions are positive, praising service quality and staff friendliness.`,
        priority: "low"
      });
    } else {
      insights.push({
        type: "recommendation",
        title: "Enhance Customer Communication",
        description: "Implement proactive check-ins with customers experiencing delays to manage expectations and reduce frustration.",
        priority: "medium"
      });
    }
  } else {
    insights.push({
      type: "recommendation",
      title: "Encourage Customer Feedback Collection",
      description: "Currently limited feedback data available. Implement feedback kiosks or QR codes to gather more customer insights.",
      priority: "medium"
    });
  }

  // Insight 4: Trend-based recommendation
  if (totalCount > 10) {
    insights.push({
      type: "finding",
      title: "Real-Time Emotion Tracking Operational",
      description: `Successfully analyzed ${totalCount} customer interactions, providing valuable sentiment data for continuous service improvement.`,
      priority: "low"
    });
  }

  return insights;
}

function broadcastEmotionUpdate(emotion: any) {
  const wss = (global as any).wss;
  if (wss) {
    wss.clients.forEach((client: any) => {
      if (client.readyState === 1) {
        client.send(JSON.stringify({ type: "emotion", data: emotion }));
      }
    });
  }
}

function broadcastFeedbackUpdate(feedback: any) {
  const wss = (global as any).wss;
  if (wss) {
    wss.clients.forEach((client: any) => {
      if (client.readyState === 1) {
        client.send(JSON.stringify({ type: "feedback", data: feedback }));
      }
    });
  }
}

